//typelist algorithm
template<typename TList> struct TypeListLength;
template<>
struct TypeListLength<NullType> {
	enum { value = 0 };
};

template<typename T, typename U>
struct TypeListLength< TypeList<T, U> > {
	enum { value = TypeListLength<U>::value + 1 };
};

//인덱스 위치에 잇는 타입 얻기
template<typename TList, int index> struct TypeListTypeAt;

template<typename Head, typename Tail>
struct TypeListTypeAt< TypeList<Head, Tail>, 0> {
	typedef Head Result;
};

template<typename Head, typename Tail, int i>
struct TypeListTypeAt< TypeList<Head, Tail>, i> {
	typedef typename TypeListTypeAt<Tail, i-1>::Result Result;
};

